<template>
    <nav class="navbar is-white is-fixed-top">
        <div class="container">
            <div class="navbar-brand">
                <div class="navbar-item">
                    <router-link class="navbar-item" to="/" style="padding: 0 0.5rem">
                        <img src="data:image/svg+xml;base64," style="max-height: 48px">
                    </router-link>
                </div>
                <div @click="menuOpen = !menuOpen" class="navbar-burger burger" :class="menuOpen ? 'is-active' : ''" data-target="mainNav"> <span></span><span></span><span></span> </div>
            </div>
            <div class="navbar-menu" :class="menuOpen ? 'is-active' : ''" id="mainNav">
                <div class="navbar-end">
                    <div class="navbar-item">
                        <router-link to="/recipe/create" class="button is-primary"><fa icon="book" size="lg" fixed-width />Publicar receta</router-link>
                    </div>
                    <template v-if="user != null">
                        <div class="navbar-item has-dropdown is-hoverable">
                            <a class="navbar-link"><fa :icon="['far', 'user-circle']" size="2x" fixed-width />{{ user.name }}</a>
                            <div class="navbar-dropdown is-boxed">
                                <router-link class="navbar-item" to="/my-recipes">
                                    <fa icon="book-open" size="lg" fixed-width />Mis recetas
                                </router-link>
                                <router-link class="navbar-item" to="/account">
                                    <fa icon="cog" size="lg" fixed-width />Cuenta
                                </router-link>
                                <router-link v-if="user.role == 'admin'" class="navbar-item" to="/admin-panel">
                                    <fa icon="toolbox" size="lg" fixed-width />Admin panel
                                </router-link>
                                <hr class="navbar-divider">
                                <a v-on:click.prevent="logout" class="navbar-item" href="#">
                                    <fa icon="sign-out-alt" size="lg" fixed-width />Salir
                                </a>
                            </div>
                        </div>
                    </template>
                    <template v-else>
                        <div class="navbar-item">
                            <a class="has-text-black" href="#" v-on:click.prevent="showRegisterModal"><fa :icon="['far', 'user-circle']" size="lg" fixed-width />Registrarse</a>
                        </div>
                        <div class="navbar-item">
                            <a class="has-text-black" href="#" v-on:click.prevent="showLoginModal"><fa icon="sign-in-alt" size="lg" fixed-width />Entrar</a>
                        </div>
                    </template>
                </div>
            </div>
        </div>
        <login-modal :class="modal.isOpen ? 'is-active' : ''"></login-modal>
    </nav>
</template>
<script>
    import { mapState } from 'vuex'
    import LoginModal from './Auth/LoginModal.vue'
    export default{
        data(){
            return{
                menuOpen: false,
            }
        },
        computed: {
            ...mapState({
                user: state => state.auth.user,
                modal: state => state.modal.modal,
            })
        },
        components: { LoginModal },
        methods: {
            showLoginModal() {
                this.$store.dispatch('modal/openModal')
                var currentModal = 'login'
                this.$store.dispatch('modal/changeModal', { currentModal })
            },
            showRegisterModal() {
                this.$store.dispatch('modal/openModal')
                var currentModal = 'register'
                this.$store.dispatch('modal/changeModal', { currentModal })
            },
            async logout () {
                // Log out the user.
                await this.$store.dispatch('auth/logout')

                // Redirect to login.
                this.$router.push({ name: 'home' })
            }
        }
    }
</script>
